package collections;

import java.util.ArrayList;

public class Example2WrapperClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int m = 10;
		Integer n = 5;//wrapper class
		float f = 10.5f;
		Float g = 11.5f;
		char c = 'r';
		Character d = 'k';
		System.out.println("this is int m: "+m);
		System.out.println("this is Integer n: "+n);
		System.out.println("this is float f: "+f);
		System.out.println("this is Float g: "+g);
		System.out.println("this is char c: "+c);
		System.out.println("this is Character d: "+d);
		
		ArrayList<Integer> al = new ArrayList<Integer>();//collection - array list with wrapper class. Here wrapper class is <Integer>

	}

}
